for(var links=document.querySelectorAll("[data-link]"),i=0;i<links.length;i++){var linkEl=links[i];linkEl.addEventListener("click",function(e){for(var t=e.target;t.parentNode;){var n=(t=t.parentNode).getAttribute("data-link");if(n)return window.location=n,null}return null})}var delay=function(){var n=0;return function(e,t){clearTimeout(n),n=setTimeout(e,t)}}(),filterForms=document.getElementsByClassName("filter-form");for(i=0;i<filterForms.length;i++){for(var form=filterForms[i],selects=form.querySelectorAll("select"),j=0;j<selects.length;j++)selects[j].addEventListener("change",function(e){form.submit()});for(var inputs=form.querySelectorAll("input, textarea"),k=0;k<inputs.length;k++)inputs[k].addEventListener("input",function(e){delay(function(){form.submit()},500)})}